package com.tiago.cursojava.aula11;

public class VariaveisPontoFlutuante {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double valorPassagem = 4.50;
		
		float valorTomate = (float) 3.95;
		float valorTomate2 = 3.95f;
		

		System.out.println("Valor da passagem = " + valorPassagem);
		System.out.println("Valor do tomate = " + valorTomate);
		System.out.println("Valor do tomate2 = " + valorTomate2);
		
	}

}
